template.template_md:
Client,Site,Host,Version,Software
{% for record in data_sources.agentsSoftware %}
  {%- set softwareFound = [] -%}
  {%- for software in record.software -%}
    {%- if software_name|lower in software.name|lower -%}
      {%- do softwareFound.append(software) -%}
    {%- endif -%}
  {%- endfor -%}
  {%- for software in softwareFound -%}
{{ record.agent__site__client__name }},{{ record.agent__site__name }},{{ record.agent__hostname }},{{software.name}},{{ software.version }}
{% endfor -%}
  {%- if not softwareFound -%}
{{ record.agent__site__client__name }},{{ record.agent__site__name }},{{ record.agent__hostname }},"Not Installed","N/A"
  {%- endif -%}
{%- endfor -%}
